import ChatProcessor from './ChatProcessor';

class MessageParser {
    constructor(actionProvider, state, createChatBotMessage) {
        this.actionProvider = actionProvider;
        this.state = state;
        this.processor = new ChatProcessor(createChatBotMessage);
    }

    parse(incomingMessage) {
        // Log the latest message
        console.log("Latest Message:", incomingMessage);

        // Log all previous messages (not including the latest)
        console.log("All Previous Messages:", this.state.messages);

        // Use the processor to get a reply
        const reply = this.processor.process(incomingMessage, this.state.messages);

        // Send the reply using the actionProvider
        this.actionProvider.sendBotResponse(reply);
    }
}

export default MessageParser;
