import { ChatOpenAI } from "langchain/chat_models/openai";
import { HumanMessage, ChatMessage, SystemMessage } from "langchain/schema";

class LangchainProcessor {
    constructor(createChatBotMessage) {
        this.createChatBotMessage = createChatBotMessage;
    }

    async process(newMessage, allMessages) {
        const chat = new ChatOpenAI({
            temperature: 0,
            openAIApiKey: process.env.REACT_APP_OPEN_AI_API_KEY
        });

        try {
            console.log(allMessages)
            // Convert all previous messages to the format expected by ChatOpenAI
            const formattedMessages = allMessages.map(msg => {
                if (msg.type === "bot") {
                    return new SystemMessage(msg.message);
                } else  {
                    // We're assuming that bot messages should be treated as SystemMessages.
                    // If that's not the case, adjust accordingly.
                    return new HumanMessage(msg);
                } 
            });

            // Add the new human message to the list
            formattedMessages.push(new HumanMessage(newMessage));

            console.log(formattedMessages);

            const result = await chat.predictMessages(formattedMessages);

            console.log(result)

            // // Extract the content from the AIMessage
            const botResponseContent = result.content;

            // // Add the AIMessage to allMessages
            // allMessages.push({
            //     type: "bot",
            //     content: botResponseContent
            // });

            return botResponseContent;

        } catch (error) {
            console.error("Error processing message with OpenAI:", error);
            return "Sorry, I faced an error processing your message.";
        }
    }
}

export default LangchainProcessor;
