import { OpenAI } from "langchain/llms/openai";

class LangchainProcessor {
    constructor(createChatBotMessage) {
        this.createChatBotMessage = createChatBotMessage;
        this.llm = new OpenAI({
            temperature: 0,
            openAIApiKey: process.env.OPEN_AI_API_KEY,
        });
    }

    async process(newMessage, allMessages) {
        try {
            const result = await this.llm.predict(newMessage);
            return "got it"; // Assuming the result is a string
        } catch (error) {
            console.error("Error processing message with OpenAI:", error);
            return "Sorry, I faced an error processing your message.";
        }
    }
}

export default LangchainProcessor;
