class ChatProcessor {
    constructor(createChatBotMessage) {
        this.createChatBotMessage = createChatBotMessage;
    }

    // This method will be called every time a new message is sent to the chatbot.
    process(newMessage, allMessages) {
        // For simplicity, let's make a basic check:
        if (newMessage.toLowerCase().includes("how are you")) {
            return "I'm just a bot, but I'm functioning well. Thanks!";
        }

        // Default reply if no conditions are met:
        return "I didn't understand that.";
    }
}

export default ChatProcessor;
